import React from 'react';
import p5 from 'p5';
import {
  createCanvas,
  cursor,
  fill,
  noStroke,
  font
} from 'p5'

class AnimationOne extends React.Component {
    constructor(props) {
      super(props)
      this.myRef = React.createRef()

      this.font = null
      this.points = null
      this.bounds = null 
    }


    Sketch = (p) => {

      // Native p5 functions work as they would normally but prefixed with 
      // a p5 object "p"
      p.setup = () => {
        //Everyhting that normally happens in setup works
        p.createCanvas(400,400)
      }

      p.draw = () => {
          // And everything that normally goes in draw in here
          p.background(0)
          p.circle(p.width / 2, p.height / 2, 50)
      }
    }

    componentDidMount() {
      this.myP5 = new p5(this.Sketch, this.myRef.current)
    }

    render() {
      return (
        <div ref={this.myRef}>
             <div id="canvas">{this.props.transcript}</div>
        </div>
      )
    } 
  }



  export { AnimationOne }
