import React from 'react';
import p5 from 'p5';

class AnimationOne extends React.Component {
    constructor(props) {
      super(props)
      this.myRef = React.createRef()
      this.points = null
      this.bounds = null 
    }

    Sketch = (p) => {
      // Native p5 functions work as they would normally but prefixed with 
      // a p5 object "p"


      p.setup = () => {
        let font = p.loadFont('https://fonts.google.com/specimen/Inter', () => p.fontReady = true);
        //Everyhting that normally happens in setup works
        p.createCanvas(1000,1000)

        this.points = font.textToPoints(
          'a piece of writing', 0, 0, 200, {
            sampleFactor: 1,
            simplifyThreshold: 0
          });

        this.bounds = font.textBounds(
          'a piece of writing that ', 0, 0, 200);

        p.cursor("CROSS");
        p.fill(255, 127);
        p.noStroke();
      }

      p.draw = () => {
          // And everything that normally goes in draw in here
          p.background(0)
          p.circle(p.width / 2, p.height / 2, 100)
          p.background(0);

          p.stroke(51);
          p.line(p.width / 2, 0, p.width / 2, p.height);
          p.line(0, p.height / 2, p.width, p.height / 2);
          p.noStroke();

          let centerDist = p.dist(p.mouseX, p.mouseY, p.width / 2, p.height / 2);

          let transparency = p.map(centerDist, 0, p.width / 2, 200, 50);
          transparency = p.constrain(transparency, 50, 200);
          p.fill(255, transparency);

          let jiggle = p.map(centerDist, 0, p.width, 1, 300);

          p.translate((p.width - p.abs(this.bounds.w)) / 2, 
                    (p.height + p.abs(this.bounds.h)) / 2);

          for (let i = 0; i < this.points.length; i++) {
            let p = this.points[i];
            p.ellipse(p.x + jiggle * p.randomGaussian(), 
              p.y + jiggle * p.randomGaussian(), 5, 5);
          }
      }
    }

    componentDidMount() {
      this.myP5 = new p5(this.Sketch, this.myRef.current)
    }

    render() {
      return (
        <div ref={this.myRef}>
             <div id="canvas">{this.props.transcript}</div>
        </div>
      )
    } 
  }



  export { AnimationOne }
