import React from 'react';
import p5 from 'p5';

class AnimationOne extends React.Component {
    constructor(props) {
      super(props)
      this.myRef = React.createRef()
    }

    Sketch = (p) => {

       p.setup = () => {
        createCanvas(windowWidth, windowHeight);

        points = font.textToPoints(
          'a piece of writing ', 0, 0, 200, {
            sampleFactor: 1,
            simplifyThreshold: 0
          });

        bounds = font.textBounds(
          'a piece of writing that ', 0, 0, 200);

        cursor(CROSS);
        fill(255, 127);
        noStroke();
       }

       p.draw = () => {
       }
    }

    componentDidMount() {
      this.myP5 = new p5(this.Sketch, this.myRef.current)
    }

    render() {
      return (
        <div ref={this.myRef}>
             <div id="canvas">{this.props.transcript}</div>
        </div>
      )
    } 
  }



  export { AnimationOne }
